import React, { Component } from 'react';
export default class About extends Component {
  render() {
    let resumeData = this.props.resumeData;
    let canvas = document.querySelector("#canvas");
canvas.width = window.innerWidth;
canvas.height = window.innerHeight;
const c = canvas.getContext("2d");
c.fillStyle = 'green';
c.font = '20px serif'
var columns = [];

animate();


function animate(){
	requestAnimationFrame(animate);
	let chance = Math.random();
	if(chance>0.8) createNewString();
	c.clearRect(0,0,window.innerWidth,window.innerHeight);
	for(let i = 0; i < columns.length; i++){
		if(columns[i].visible()){
			columns[i].show();
			columns[i].update();
		}else{
			columns.splice(i,1);
		}
	}
}
function createNewString(){
	let r = new Column(Math.random()*window.innerWidth,Math.random()-0.5,Math.floor(Math.random()*20+7));
	columns.push(r);
}
class Column{
	constructor(x=1,z=1,length=10){
		this.x = x;
		this.z = z;
		this.l = length;
		this.word = this.generateWord();
		this.vel = this.calculateVel();
		this.font = this.calculateFont();
		this.y = -((this.font+1)*this.l);
	}
	calculateFont(){
		return Math.abs(this.z)*50;
	}
	calculateVel(){
		return Math.abs(this.z*1.5)+.5;
	}
	generateWord(){
		let w ="";
		for(let i=0;i<this.l;i++)
	 		w =w+""+Math.random().toString(36).substr(2,1);
		return w;
	}
	changeLetter(index){
		let pred = '';
		let succ = '';
		let letter = Math.random().toString(36).substr(2,1);
		for(let i = 0; i < this.l; i++){
			if(i < index) pred = pred+""+this.word[i]; 
			else if(i > index) succ = succ+""+this.word[i]; 
		}
		this.word = pred+""+letter+succ;
	}
	update(){
		this.y +=this.vel;
	}
	show(){
		for(let i = 0; i < this.l; i++){
			c.font ="bold "+this.font+"px serif";
			c.fillText(this.word[i], this.x, this.y+i*this.font);
			let change = Math.random();
			if(change>0.98) this.changeLetter(i);
		}
	}
	visible(){
		if(this.y<window.innerHeight)
			return true;
		return false;
	}
}
    return (
      <section id="about">
         <div className="row">

            <div className="three columns">

               <img className="profile-pic"  src="images/moi.jpg" alt="" />

            </div>

            <div className="nine columns main-col">

               <h2>A propos</h2>
               <p>
               {
                 resumeData.aboutme
               }
               </p>

               <div className="row">

                  <div className="columns contact-details">

                  <h2>Détails du contact</h2>
                  <p className="address">
       						<span>{resumeData.name}</span>
                     <br></br>
       						   <span>
                     {resumeData.num}
                    </span>
                    <br></br>
                    <span>{resumeData.email}</span>
       					   </p>
                  </div>
               </div>
            </div>
         </div>
      </section>
    );
  }
}
